#define PI 3.141592
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = fragCoord-iResolution.xy/2.;
    float ir = 100./length(coord);

    float a = atan(coord.y, coord.x);
    float b = acos(ir>1.?1./ir:ir);
    vec2 t = 20.*vec2(a+b, a-b)/PI;
    t = mod(floor(t+vec2(-1.5, 0.5)*iTime), 2.);

    fragColor = vec4(mod(t.x+t.y,2.));
}
