void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord;

    //uv.x *= sin(iTime/10.);
    //uv.y *= cos(iTime/10.);


    float uvx = floor(uv.x/10.);
    float uvy = floor(uv.y/10.);

    float agg = uvx + uvy;


    float col = mod(agg,2.);



    fragColor = col * vec4(1.,1.,1.,1.0);


}
