void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 6. * (fragCoord - .5 * iResolution.xy) / iResolution.y;

    uv -= round(uv);
    vec2 st = vec2(atan(uv.x, uv.y), length(uv));

    uv = vec2(st.x/6.2831 + .5 + iTime*.1, st.y);
    float x = uv.x * 5.;

    float m = min(fract(x), fract(1. - x));

    float c = smoothstep(0., .1, m-uv.y*2.+.2);


    // Output to screen
    fragColor = vec4(c * vec3(.1+ 1.*(1.-fract(iTime)), .8* fract(iTime), .1), 1.);
}
