void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 mouse = iMouse.xy == vec2(0) ? (0.5 + 0.5 * vec2(cos(iTime), sin(iTime * 2.0))) * iResolution.xy : iMouse.xy;
    fragColor = vec4(0.0);
    vec4 rect = vec4(0.0, 0.0, iResolution.xy);
    for (int i = 0; i < 16; i++) {
        float mid = (rect.x + rect.z) * 0.5;
        if (fragCoord.y >= rect.y && fragCoord.y < rect.w) fragColor += smoothstep(1.0, 0.0, abs(fragCoord.x - mid));
        if (mouse.x < mid) rect.z = mid;
        else rect.x = mid;
        mouse = mouse.yx;
        fragCoord = fragCoord.yx;
        rect = rect.yxwz;
    }

    fragColor.rgb = mix(fragColor.rgb, vec3(1.0, 0.0, 0.0), smoothstep(1.0, 0.0, length(fragCoord - mouse) - 8.0));
}
