// Original slit scan tunnel by Roywig : https://www.shadertoy.com/view/4dtSWS

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord.xy - iResolution.xy) / iResolution.y;
	vec3 color = vec3(0);
    float rInv = 1./length(uv*2.);
    uv = uv* rInv;

    float bump_height = 5.;
    float v1 = texture(iChannel1,uv+vec2(0.005,0.0)-vec2(rInv+iTime,0.)).r;
	float v2 = texture(iChannel1,uv+vec2(-0.005,0.0)-vec2(rInv+iTime,0.)).r;
	float v3 = texture(iChannel1,uv+vec2(0.0,0.005)-vec2(rInv+iTime,0.)).r;
	float v4 = texture(iChannel1,uv+vec2(0.0,-0.005)-vec2(rInv+iTime,0.)).r;
    float bumpx = (v2-v1)*bump_height;
    float bumpy = (v3-v4)*bump_height;

    float light = 1.-length(vec2(uv+rInv-1.)-vec2(bumpx,bumpy));

    color = texture(iChannel0,uv-vec2(rInv+iTime,0.)).rgb*light*3.;
    fragColor = vec4(color,1.0);
}
