void mainImage( out vec4 O,  vec2 U )
{
    //float time = /* Get the current time in seconds */;

    // Animate between 5 and 8 using a sine function
    float frequency = 0.5; // Adjust the frequency of the oscillation
    float minValue = 6.0;
    float maxValue = 7.5;
    float animatedValue = mix(minValue, maxValue, 0.5 * (1.0 + sin(iTime * frequency)));

    // Increase the thickness of the maze lines
    float thicknessFactor = 2.0; // Adjust this factor to control the thickness
    float distance = thicknessFactor * length(ceil(U /= 40.));

    // Use the animated value with the modified distance in your original code
    vec4 mazeColor = vec4(1.0, 0.2, 0.0, 1.0); // Orange color
    O += 0.05 / fract(sin(animatedValue * distance) < 0. ? U.x : U.y) * mazeColor - O;
}
