vec3 palette( float t ) {
    vec3 a = vec3(5, 10, 15);
    vec3 b = vec3(15, 10, 5);
    vec3 c = vec3(1.0, 2.5, 10.0);
    vec3 d = vec3(0.1,0.3,0.5);

    return a + b*sin(10.314*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord * 5.0)/iResolution.xy;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    // Time varying pixel color
    vec3 col = 1.5 + 0.5*sin(iTime+uv.xyx+vec3(10,2,4));

    for (float i = 0.0; i < 10.0; i++) {
        uv = fract(uv * 10.0) - 2.5;

        float d = length(uv0) * exp(length(uv0));

        vec3 col = palette(length(uv0) + i*.8 + iTime*.8);

        d = cos(d*4. - iTime)/5.;
        d = sin(d);

        d = pow(0.1 / d, 1.2);

        finalColor += col * d;
    }

    // Output to screen
    fragColor = vec4(finalColor,1.0);
}
