void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime * 15.0;
    float r = (1.0 + cos((time * 6.0 + fragCoord.x * 0.5) * 0.0142857142857143) + sin((time * 3.0 + -fragCoord.y * 0.5) * 0.0142857142857143) * 0.25) * 0.5;
    float g = (0.5 + cos(r * 1.0 +
                         (time * 4.0 + fragCoord.y * 0.5) * 0.02) + sin((time * 10.0 + fragCoord.x * 0.5) * 0.02) * 0.666666666666667);

    float sum = (r * 1.8 + g - 0.15) * 0.5;
    float b = 1.0 - sum;

    fragColor = vec4(r * 1.05, g, b, 1.0);
}
