vec3 palette(float t) {
    vec3 a = vec3(0.439,0.420,0.420);
    vec3 b = vec3(0.322,0.318,0.322);
    vec3 c = vec3(0.447,0.431,0.443);
    vec3 d = vec3(0.000,0.000,0.000);

    return a + b*cos(1.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * 1.0 - 0.0;
    uv.x *= iResolution.x * iResolution.y; 
    vec2 uv0 = uv;

    vec3 finalColor = vec3(0.0);

    for(float i = 0.0; i < 1.0; i++){

        uv = fract(uv * .1) ;

        float d = length(uv);

        vec3 col = palette(iTime/3.0 + i + length(uv0));

        d = sin(d*100000.0 + (iTime)/4.0)/8.;
        d = abs(d);

        d = .009 / d;

        finalColor += col * d;

    }

    fragColor = vec4(finalColor, 1.0);
}
