//522  chrs by FabriceNeyret2 
#define dC(u,r)  smoothstep( r+2./R.y, r, length(u)) // fn circle
#define V(d,a)  d * vec2(cos(a),sin(a))

void mainImage(out vec4 f, vec2 c){
    vec2   R = iResolution.xy,
           u = 1.5*( c - .5*R ) / R.y, p; 
    float pi = 3.14, l,i,
           t = sin(iTime*.5)*.5+.5, s=.6, icr=.1,  // time, space, inner c. radi
           C = dC( abs(u) - vec2(s,0), icr), S;
    p.x = mix(-s,s, smoothstep(-.5,.5,sin(iTime*.2)) );  // swap time

    for( ; i++ < 6.; )
        S += dC( u - p + V( mix(.38,.25,t), i* pi/3.),  mix(.19,.04,t) );

    for( i-=i ; i++ < 8.; )     
        S += dC( u + p + V( mix(.15,.25,t), i* pi/4.),.04 );

    if (iMouse.z>0. )
        l = step( abs(u.x) , mix(s+.1,.1,t) ) * dC( icr-abs(u.y) ,0.);

    f = mix( vec4(1), C*vec4(1,.5,0,0) + S*.5, C+S+l );
}

