void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;

    vec3 col = vec3(0.0);

    vec3 col_multiplier = vec3(1.0);

    const float size_divider = 3.0;

    float bloom_size = 0.05;

    for (float i = 0.0; i<4.0; i++) {

        uv *= size_divider;

        bloom_size *= size_divider/2.0;

        float d = length(uv);

        d = bloom_size/abs(sin(d*d*0.5-iTime*0.3));

        col += col_multiplier*d;

        if (col_multiplier==vec3(1.0)){
            col_multiplier= vec3(1.0,0.0,0.0);
        }
        else {
            col_multiplier = col_multiplier.brg;
        }
    }

    fragColor = vec4(col,1.0);
}
