vec3 palette(float t) {
    vec3 a = vec3(1.000,1.000,1.000);
    vec3 b = vec3(1.000,0.000,0.584);
    vec3 c = vec3(1.000,0.000,0.682);
    vec3 d = vec3(1.000,0.000,0.435);

    return a + b*cos(6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y; 
    vec2 uv0 = uv;

    uv = -cos(floor(uv * 999.0) -0.5+iTime);

    float d = length(uv);

    vec3 col = palette(d + length(uv0));

    d = step(cos(d/0.01 + (iTime)*10.25), .01);


    d = 0.3 / d;

    col /= d*cos(iTime*55.);

    fragColor = vec4(col, 1.0);
}
