#define R3D(a) mix(q*dot(p,q), p, cos(a)) + sin(a)*cross(p,q)

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,t=iTime; vec3 p,q=vec3(-2,1,-1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e)*2.;
    p.z-=2.;
    p=R3D(t*.5);

    for(j=0.;j++<16.;)
        p=abs(abs(p-.5)-.02)-.03,
        d=max(min(p.z,p.y),p.x),
        d-=dot(p,q)/1e4;

    e-=d;

}o+=vec4(exp(1./p),1)*8./i;
}
