void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 claude = texture(iChannel1, uv);

    if (!(claude.r > 0.5 || claude.b > 0.5) && claude.g > 0.5) {
        claude.g = 0.0;
        claude.a = 0.0;
    }

    // Time varying pixel color
    vec3 col = texture(iChannel0, uv).xyz;
    float bw = (col.r + col.g + col.b) / 2.0;
    col = vec3(bw * uv.x, bw * uv.y, bw);

    if (claude.a == 0.0) {
        claude.rgb = col.rgb;
    }

    // Output to screen
    fragColor = claude;
    //fragColor = vol;
    //fragColor = texture(iChannel0, uv);
}
