void mainImage(out vec4 fragColor, in vec2 fragCoord)
{       
    float width = iResolution.x / 2.0;
    float offset = float((iFrame * 16) % int(iResolution.x + width)) - width;
    float edge = offset + width;

    float x = fragCoord.x;
    vec3 col = vec3(0.0);

    if (x >= offset && x <= edge) {
        col = vec3(1.0);
    }


    fragColor = vec4(col,1.0);
}
