void mainImage( out vec4 C, in vec2 O )
{
    C=texelFetch(iChannel0,ivec2(O),0);
}

vec2 fun1(vec2 p,float f1,float f2){// Elliptical Grid Mapping
    vec2 o;
    o.x=p.x*pow(1.-pow(abs(p.y),f2)/f2,1./f2);
    o.y=p.y*pow(1.-pow(abs(p.x),f1)/f1,1./f1);
    return o;
}// use for variable a, example a=(fun1(U,m.x,m.y)).x*0.1

vec2 fun2(vec2 p,float f1,float f2){//Squelched Grid Open Mapping
    vec2 o;
    o.x=p.x*pow((1.-pow(abs(p.y),f2))/(1.-pow(p.x,f1)*pow(p.y,f2)),1./f2);
    o.x=p.y*pow((1.-pow(abs(p.x),f1))/(1.-pow(p.y,f2)*pow(p.x,f1)),1./f1);
    return o;
}// use for variable a, example a=(fun2(U,m.x,m.y)).x*0.1



void mainImage( out vec4 C, in vec2 O )
{
    vec2 m=vec2(2.,2.);//change for different patterns
    vec2 n=vec2(1.,1.);//change for different patterns
    float a=0.1;//change for different patterns
    float r=1.;//change for different patterns
    float b=0.25;//change for different patterns

    vec2 U=O/iResolution.xy-0.5;

    float an = iTime*r,
          s = sin(an),
          c = cos(an);

    U*= mat2( c, s, -s,  c);
    float T=iTime;
    //a=(fun2(U,m.x,m.y)).x*0.1;

    C=texelFetch(iChannel0,ivec2(O),0);
    if(iFrame==0){C=vec4(1,0,0,1);}
    if(pow(abs(U.x),T*m.x)+pow(abs(U.y),T*m.y)<=b*(pow(abs(U.x),T*n.x)+pow(abs(U.y),T*n.y))){
        C.xyz=hsv2rgb(rgb2hsv(C.xyz)+vec3(a,0,0));
    }

}

vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}
