// continuous 2-lobes membrane variant of "+- dipolar emission" https://shadertoy.com/view/Xcl3z7


void mainImage( out vec4 O, vec2 u )
{
    vec2   R = iResolution.xy,
           P = ( 2.*u - R ) / R.y;              // normalized coordinates
    float  f = 10., 
           a = iMouse.z > 0.                    // distance between emitters
                 ? .3*iMouse.x/R.x              //     mouse control
                 : .15+.15*cos(iTime),          //     demo mode
           t = 10.*iTime,
           I = 0.;

   for( float k=-1.; k < 1.; k += .01 ) {         
        float d  = length( P - vec2(k*a,0) );   // distance from P to emitter sample          
        I +=  sin( 6.28*f*d - t ) / (d*d)       // wave
            * sin( 3.14*k);                     // amplitude amplitude along the emitting segment
    }     
    O = I/100. * .2* vec4(-1,1,0,0);            // coloring ( red: negative green: positive ) 
   // O *= O;
}
