// Modified version of @DamnXYZ's effect - License Unkown - https://www.shadertoy.com/view/wdBcW3
vec3 distort_space(vec3 p){
    float osc = .5 + .5*sin(iTime);
    p.x += osc * sin(p.z)*.5;
    p.y += osc * cos(p.z)*.5+.5;
    return p;
}


// This function let's us jump from 2D-UV to spherical 3D-XYZ position
vec3 uvTo3D(vec2 uv) {
    // The 2-Sphere point via UV => Lat/Long angles
    float theta = uv.x * 2.0 * 3.14159265359; // Longitude
    float phi = uv.y * 3.14159265359; // Latitude
    // Trigonometry from 2-Sphere angles to an XYZ normalized [-1,1].
    float x = sin(phi) * cos(theta);
    float y = sin(phi) * sin(theta);
    float z = cos(phi);
    // { Dev Note }
    // If you're porting this shader to a material, I strongly recommend you skip this function and just use the XYZ of your 'varying vNormal' in place of the result you would get here. Should be suitable for all spheres and most round geometries
    return vec3(x, y, z);
}

float sdfLine(vec3 p) {
    float radius = .002;
    //p*=1.2+sin(iTime/10.)/2.;
    // 3 Techniques below from @shadertoyjiang ðŸ™ - License Unkown - https://www.shadertoy.com/user/shadertoyjiang 
    // From comment on previous attempt > https://www.shadertoy.com/view/4csBzf
    p*=.5;

    p = p-round(p);
    p = abs(p);
    vec3 m = step(p.yzx,p)*step(p.zxy,p); // The largest component becomes "1", and the other two components become "0".
    p -= p * m;
    return length(p)-.002;



    p = p-round(p);
    p = abs(p);
    float maxxyz = max(p.x,max(p.y,p.z)),
          lengthpp = dot(p,p);    
    return sqrt(lengthpp-maxxyz*maxxyz)-.002;   



    p = p-round(p);
    p = abs(p);
    if(p.x<p.y)p.xy=p.yx;
    if(p.x<p.z)p.xz=p.zx;
    return length(p.yz)-.002;


    float closestXLine = length(vec2(round(p.yz))-p.yz)-radius;
    float closestYLine = length(vec2(round(p.xz))-p.xz)-radius;
    float closestZLine = length(vec2(round(p.xy))-p.xy)-radius;
    return min(closestXLine,min(closestYLine,closestZLine));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor= vec4(0.);
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 pos = uvTo3D(uv); // UV => 3D for equirectangular/spherical mapping


    // Simple raymarching blog reference: https://michaelwalczyk.com/blog-ray-marching.html
    const float MINIMUM_HIT_DISTANCE = 0.02;//0.001;
    //int NUMBER_OF_STEPS = 26+int(25.*sin(iTime));
    const int NUMBER_OF_STEPS = 25;
    float total_distance_traveled = 0.;
    // Fix RO overshoot
    pos*=.85;
    float distance_to_closest;
    //fragColor=(vec4(sdfLine(pos)));
    for (int i = 0; i < NUMBER_OF_STEPS; ++i){
        vec3 current_position = pos/1.2+iTime + total_distance_traveled * pos;
        distance_to_closest = sdfLine(distort_space(current_position));
        //fragColor.z+=sqrt(length(current_position));
        if (distance_to_closest < MINIMUM_HIT_DISTANCE) // hit
        {
            //fragColor.x=(1./total_distance_traveled);
            //fragColor.y=(1./total_distance_traveled);
            //fragColor.w=1.;

            //fragColor.z=1.;//fragColor.z/(fragColor.z);
            //fragColor*=pow(.02/total_distance_traveled, .2);
            //fragColor.z=fragColor.z;
            break;
        }

        //fragColor.x = normalize(fragColor.x);//
        //fragColor.y = normalize(fragColor.y);//
        //fragColor.z = normalize(fragColor.z);//
        //fragColor.w = 
        //fragColor. = normalize(fragColor.)

        total_distance_traveled += distance_to_closest;
        //if(total_distance_traveled>2.){break;}
    }
    //fragColor=normalize(fragColor);
    //fragColor = normalize(fragColor);

    // Technique from @alro - MIT License - https://www.shadertoy.com/view/WdK3Dz
    fragColor.rgb += 10.0*vec3(smoothstep(0.02, 0.01,distance_to_closest));

    //fragColor.rgb += 10.0*vec3(smoothstep(0.02+(sin(iTime)+1.)/10., 0.01+(sin(iTime)+1.)/10.,distance_to_closest));
    fragColor.rgb += pow(.2/distance_to_closest, 1.3)*vec3(.2,.01,.4);
    //fragColor *= pow(.02/total_distance_traveled, .2);
}

