#define PI 3.14159

const vec3
black = vec3(0.),
white = vec3(1.),
a = vec3(.5),
b = vec3(.5),
c = vec3(1.),
d = vec3(0., .33, .67);

vec3 palette(float t) {
    return a + b * cos(2. * PI * (c * t + d));
}

mat2 rot(float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, -s, s, c);
}

float f(vec2 uv, float r) {
    float d = length(uv);
    d = sin(d * 20. + iTime);
    float a = atan(uv.y, uv.x);
    float r2 = r * (sin(a * 20.) * 2. * (cos(.8 * iTime) + 1.));
    float v = abs(d - r2);
    v = .08 / v;
    return v;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv0 = (2. * fragCoord - iResolution.xy) / iResolution.y;

    vec2 uv = uv0;
    float d0 = length(uv0);
    vec3 col = black;

    for (float i = 0.; i < 3.; i++) {
        float d = length(uv);
        float v = f(uv, 1.);
        float t = d + d0;
        col += mix(black, palette(t), v);
        uv = fract(uv * exp(i)) - .5;
    }

    fragColor = vec4(col,1.0);
}
