vec3 palette(float t)
{
    vec3 a = vec3(0.668, 0.500, 0.668);
    vec3 b = vec3(-0.042, 0.008, 0.048);
    vec3 c = vec3(-3.142, -3.142, -0.662);
    vec3 d = vec3(0.000, 2.938, 0.667);
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    uv.xy += sin(fragCoord.yx) / 2.;

    float d = length(uv0);

    float b = abs(sin(d * 8. + iTime) / 8.);
    b = .02 / b;

    vec3 col = 0.5 + 0.5 * cos(iTime+uv.yyy + vec3(0, 2, 4));
    col *= d;
    col = 0.1 / col;

    col = smoothstep(vec3(0.), vec3(1), col);

    col *= b;

    fragColor = vec4(col, 1.0);
}
