#define R3D(a,q) ( mix( q*dot(p,q), p, cos(a) ) + sin(a)*cross(p,q) )
#define R2D(a)       mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p,R=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*R.xy)/R.y,1);
    p.z-=2.;
    p.xz*=R2D(iTime*.1);

    for(j=0.;j++<4.;)
        p=abs(abs(mod(p*R3D(5.77,vec3(1)),.2)))-.005,
        d=log(length(p.xz))*atan(p.x,p.y),
        d=dot(p,p)/2e4;
    e+=d;
}o+=vec4(sqrt(p),1)*8./i;
}
