vec2 triangle_wave(vec2 a){
    vec2 a2 = vec2(1.,0.5),
    a1 = a+a2;
    return abs(fract((a1)*(a2.x+a2.y))-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 1.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv.x += iTime/t1/12.0;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    float scale = 1.5;
    vec2 t2 = vec2(0.);
    for(int k = 0; k < 9; k++){
        uv =
            (uv+t2)/scale
            //max(abs(.5+uv+t2),abs(-.5+uv+t2))/scale
        ;
        t2 = triangle_wave(uv+.5);//if(uv.x>1./2.)
        uv = t2-triangle_wave(uv.yx);
        vec2 uv1 =
            triangle_wave((uv-.5)/1.5)-triangle_wave((uv.yx)/1.5)
            //triangle_wave((uv-.5)/1.5)-triangle_wave((uv.yx-fract(t2/2.))/1.5)
        ;    
        col.x =
            //max(t2.x,col.x)
            max(max(max(t2.y,t2.x),abs(uv1.y-uv1.x))/3.,col.x)
            //max(max(.5-min(t2.y,t2.x),abs(uv1.y-uv1.x))/3.,col.x)
            //max(max(max(uv1.y,uv1.x),abs(t2.y-t2.x))/3.,col.x)
            //max(max(max(t2.y,t2.x),abs(uv1.y-uv1.x)*3.)/3.,col.x*.75)
            //max(max(max(t2.y,t2.x),abs(uv1.y-uv1.x))/3.,col.x*.75)
            //max(max(max(t2.y,t2.x)*3.,abs(uv1.y-uv1.x))/3.,col.x)
        ;
        col = abs(col-(1.-col.x))
        //col = abs(col.yzx-(1.-col.x))
        ;
    }
    fragColor = vec4(col,1.0);
    //fragColor = pow(fragColor ,vec4(1./1.8)); //gamma correction
}
