void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float aspect = iResolution.y/iResolution.x;
    float value;
	vec2 uv = fragCoord.xy / iResolution.x;
    uv -= vec2(0.5, 0.5*aspect);
    uv *= vec2(2.0);
    uv += vec2(iTime*0.1, iTime*0.06);

    float s = 0.06;
    float s2 = 0.36;
    float v2 = 0.36;
    float w = 0.01;

    vec3 bg = vec3(0.0);
    vec3 fg = vec3(0.62, 0.51, 0.11);
    vec3 tg = fg;

    vec3 col = bg;

    //Horizontal Offset
    if (mod(uv.y, v2*2.0) < s2-6.0*w) {
        uv -= vec2(s2, 0);
        }
    vec2 llc = vec2(uv.x - mod(uv.x, s2)+s2/2.0, uv.y - mod(uv.y, v2));


    float xd = llc.x-uv.x;
    float yd = llc.y-uv.y;
    float sd = sqrt(pow(xd,2.0)+pow(yd,2.0));
    bool xp = xd<0.0;
    tg -= vec3(yd*2.5);

    //Columns
    if ((mod(uv.x, s) < w || mod(uv.x, s) > s-w) && abs(xd) > s-w) {
        col = fg;
        }

    //Horizontal columns
    if (mod(uv.y, v2) < s*4.0+w && mod(uv.y, v2) > s-w && ((xd < 0.0 && xd > -s+w) || (xd > 0.0 && xd < 0.06-w))) {
        if (mod(uv.y, s) < w || mod(uv.y, s) > s-w) {
            col = tg;
            }
        }

    //Flip
    if (mod(uv.x, s2*2.0) > s2) {
        xp = xd>0.0;
        }

    //Arches
    if (sd>s-w && xp && sd<s*3.0) {
        col=tg;
        }
    if (sd>s+w && xp && sd<s*3.0) {
        col=bg;
        }
    if (sd>s*2.0-w && xp && sd<s*3.0) {
        col=tg;
        }
    if (sd>s*2.0+w && xp && sd<s*3.0) {
        col=bg;
        }
    if (sd>s*3.0-w && xp && sd<s*4.0) {
        col=tg;
        }
    if (sd>s*3.0+w && xp && sd<s*4.0) {
        col=bg;
        }
    if (sd>s*4.0-w && xp && sd<s*4.0+w) {
        col=tg;
        }

    // Output to screen
    fragColor = vec4(col,1.0);
}
