// hello community i am a new member and a fan of shaders i started lately learning
// it was so amazing and i want to thank big wings (yt: the art of code ). also Inigo quilez AKA iq
// and others who make it easier for the newbies like me
// i still don't know to morph things back and fourth so i need guidance :)
// thank you and have fun
#define NUM_LAYERS 4.

float random2d(vec2 coord){
    return fract(sin(dot(coord.xy, vec2(12.9898, 78.233))) * 43758.5453);
}
float flower(vec2 uv ,float size,float a){
    float c = 0.;    
     // change place  

    vec2 polar = vec2(atan(uv.x,uv.y),length(uv) -size);// size
    uv = vec2(polar.x/6.2831+iTime*.2,polar.y );
    float x = uv.x*a;
    float m = min(fract(x),1.-fract(x));
    c += smoothstep(0.,.1 ,m*.5+.2-uv.y);
    return c;
}
vec3 Flowerlayout(vec2 uv){

    vec3 col = vec3(0 );
    vec2 gv = fract(uv) -.5;
    vec2 id = floor(uv);
    for(int y = -1; y <= 1;y ++){
        for(int x =-1; x<= 1; x ++){
            vec2 offs = vec2(x,y );
            float n = random2d(id+offs);
            float size = fract(n*325.2984);
            float a = clamp(.1,8.,iTime);

            float star = flower(gv-offs-(vec2(n,fract(n*10.))-.5),smoothstep(.2,.1,size)*.8,a);
            vec3 color = sin(vec3(.6,.8 ,.2 )*fract(n*6132.6)*16.2831)*.5+.5;
            star *= sin(iTime*3.+n*6.2831)+1.;
            col += star *size* color;
        }
    }
    return col ;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = (fragCoord.xy * 2. - iResolution.xy) /iResolution.y;
    vec3 col = vec3(0);
     for(float i =0.; i< 1.; i+=1./ NUM_LAYERS){
        float t = iTime*.02;
        float depth = fract(i+t);
        float fade = depth*smoothstep(1.,.9 ,depth );
        float scale = mix(20.,.5,depth);
        col += Flowerlayout(uv*scale+i*664.4)*fade;
    }



    fragColor = vec4(col ,1.0 );
}
