#define rot(x) mat2(cos(x+vec4(0,11,33,0)))

//formula for creating colors;
#define H(h)  (  cos(  h + vec3(1,2,3)   )*.6 + .5 )

#define H2(h)  (  cos(  h + vec3(1,2,0)   )*.7 + .2 )

//formula for mapping scale factor 
#define M(c)  log(1.+c)

#define R iResolution

void mainImage( out vec4 O, vec2 U) {

    O = vec4(0);

    vec3 c=vec3(0);
    vec4 rd = normalize( vec4(U-.5*R.xy, .7*R.y, R.y))*1000.;

    float sc,dotp,totdist=0., tt=iTime/3., t=0.;

    for (float i=0.; i<100.; i++) {

        vec4 p = vec4( rd*totdist);

        p.xyz += vec3(0,0,4.-20.*mod(9.+iTime/4.,6.)); 

        p.xz *= rot( 2.5 + tt + abs(sin(tt/2.)));

        p.yzw = p.xyz;

        sc = 1.; 

        p.zw *= rot(1.+tt);

        vec4 w = p;

        for (float j=0.; j<8.; j++) {

            p = abs(p)*.55;

            dotp = max(1./dot(w,w),.1);
            sc *= dotp; 

            p = p * dotp - .85; 

            vec4 p1 = exp(-p);
            vec4 p2 = exp(-p*p);
            vec4 p3 = exp(-p*p*p);


            w = .8*log(1.+log(1.+log(1.+p1)*p2)*p3) - .5; 


        }

        float dist = abs( length(p.zw)-.1)/sc ;  
        float stepsize = dist/10. + 5e-5 ;     
        totdist += stepsize;                 

        c +=
             .01 * H2(  atan( p.w, p.z )) 
           + .02 * mix( vec3(1), H(M(sc)),.95) *  exp(-i*i*stepsize*stepsize*1e3);
    }

    c = 1. - exp(-c*c);
    O = ( vec4(c,0) );

}
