void mainImage(out vec4 c, in vec2 v) {                     // Simple Egg.
    v = v / iResolution.y - vec2(0.9, 0.4);                 // Center and scale.
    v = 0.8 * length(v) + 0.2 * sqrt(dot(v, v) - v + 0.25); // Weighted average of 
    c = fract(v - 0.5).yyyy;                                // smooth abs y and disc.
}                                                           // By Rik Riesmeijer.
