void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;
    float result = 0.2;
    result += texture(iChannel0, uv * 1.2 + vec2(iTime*-0.001)).r;
    result *= texture(iChannel0, uv * 0.8 + vec2(iTime*+0.001)).g;
    result = pow(result, 48.0);
    fragColor = vec4(0.25, 0.25, 1., 1.)*result;
}
