void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(0,0,0) + (pow((1.0-2.0*abs(uv.y-0.5)),4.7))*abs(sin(vec3(0,iTime,0.2))*sin(uv.x*3.0+iTime/2.23)+0.15);

    col += + pow((1.0-2.0*abs(uv.y-0.5)),5.7)*abs(sin(vec3(0,0,iTime))*sin(uv.x*2.0+iTime/3.0+0.3)*0.9+0.1);
    // Output to screen
    fragColor = vec4(col,1.0);
}
