// Modeling - 2024.02.06
// Eric Galin

// Rotation matrix around z axis
// a : Angle
mat3 Rz(float a)
{
  float sa=sin(a);float ca=cos(a);
  return mat3(ca,sa,0.,-sa,ca,0.,0.,0.,1.);
}

struct Ray {
vec3 ro; // Ray origin
vec3 rd; // Direction
};

// Compute a point on the ray
// ray : Ray
// t   : depth
vec3 Point(Ray ray,float t)
{
  return ray.ro+t*ray.rd;
}

// Compute the ray
//     m : Mouse position
//     p : Pixel
Ray CreateRay(vec2 m,vec2 p)
{
  float a=3.*3.14*m.x;
  float le=3.5;

  // Origin
  vec3 ro=vec3(35.,0.,10.)*Rz(a);

  // Target point
  vec3 ta=vec3(0.,0.,3.);

  // Orthonormal frame
  vec3 w=normalize(ta-ro);
  vec3 u=normalize(cross(w,vec3(0.,0.,1.)));
  vec3 v=normalize(cross(u,w));
  vec3 rd=normalize(p.x*u+p.y*v+le*w);
  return Ray(ro,rd);
}

// Primitives

// Sphere
// p : point
// c : center of skeleton
// r : radius
float Sphere(vec3 p,vec3 c,float r)
{
  return length(p-c)-r;
}

// Plane
// p : point
// c : center of skeleton
// n : Normal
float Plane(vec3 p,vec3 c,vec3 n)
{
  return dot(p-c,n);
}

// Operators

// Union
// a,b : field function of left and right sub-trees
float Union(float a,float b)
{
  return min(a,b);
}

// Union, extension to four sub-trees
// a,b,c : field function of left and right sub-trees
float Union(float a,float b,float c,float d)
{
  return min(min(a,b),min(c,d));
}

// Difference
// a,b : field function of left and right sub-trees
float Difference(float a,float b)
{
  return max(a,-b);
}

// Potential field of the object
// p : point
float object(vec3 p)
{

  float v=  Sphere(p,vec3(3.,0.,6.),4.);

  v=Union(v,
    Sphere(p,vec3(0.,2.,3.),3.));

  float d=Union(
      Sphere(p,vec3(5.,1.,5.),3.),
    Sphere(p,vec3(-1.,1.,6.),3.),
    Sphere(p,vec3(-1.,2.,2.),2.),
    Sphere(p,vec3(1.,1.,6.),2.));
  v=Difference(v,d);

  v=Union(v,
    Plane(p,vec3(0.,0.,-1.),vec3(0.,0.0,1.0))
  );

  return v;
}

// Analysis of the scalar field

const int Steps=200;// Number of steps
const float Epsilon=.01;// Marching epsilon

// Object normal
// p : point
vec3 ObjectNormal(vec3 p)
{
  const float eps=.001;
  vec3 n;
  float v=object(p);
  n.x=object(vec3(p.x+eps,p.y,p.z))-v;
  n.y=object(vec3(p.x,p.y+eps,p.z))-v;
  n.z=object(vec3(p.x,p.y,p.z+eps))-v;
  return normalize(n);
}

// Trace ray using ray marching
// ray : Ray
// e : Maximum distance
// h : hit
// s : Number of steps
float SphereTrace(Ray ray,float e,out bool h,out int s)
{
  h=false;

  // Start at the origin
  float t=0.;

  for(int i=0;i<Steps;i++)
  {
    s=i;
    vec3 p=Point(ray,t);
    float v=object(p);
    // Hit object
    if(v<0.)
    {
      h=true;
      break;
    }
    // Move along ray
    t+=max(Epsilon,v);
    // Escape marched too far away
    if(t>e)
    {
      break;
    }
  }
  return t;
}

// Lighting

// Background color
// d : Ray direction
vec3 Background(vec3 d)
{
  return mix(vec3(.45,.55,.99),vec3(.65,.69,.99),d.z*.5+.5);
}
// Ombre
// p : Le point pour lequel on vérifie s'il y a une ombre.
// lum : La position de la source de lumière.

bool ombre(vec3 p,vec3 lum)
{

 Ray ray=Ray(p,normalize(lum-p));
 bool hit;
 int s;
 float t=SphereTrace(ray,length(lum-p),hit,s);

 return hit;

}

// sphericalFibonacci
// i : L'indice du point à générer.
// n : Le nombre total de points à générer.

vec3 sphericalFibonacci(int i,int n)
{
    float phi = 2. * 3.1415 * float(i) * (2. / (3. - sqrt(5.)));
    float theta = acos(1. - 2. * float(i) / float(n));
    return vec3(sin(theta) * cos(phi), sin(theta) * sin(phi), cos(theta));
}

// ombredouce
// p : Le point pour lequel on calcule l'ombre douce.
// lump : La position de la source de lumière.
// tailleL : La taille de la source de lumière.
// n : Le nombre d'échantillons utilisés pour calculer l'ombre douce.

float ombredouce(vec3 p,vec3 lump,float tailleL, int n)
{
    float ombref = 0.;

    for (int i = 1; i <= n; i++) {
        vec3 l = lump + sphericalFibonacci(i, n)*tailleL;
        ombref+=float(ombre(p,l));
    }
     return ombref/float(n);
}


// Shading and lighting
// p : Point
// n : Normal at point
// e : Eye direction

vec3 Shade(vec3 p,vec3 n,vec3 e)
{
  // Point light
  const vec3 lp=vec3(5.,30.,30.);

  // Light direction to point light
  vec3 l=normalize(lp-p);

  // Ambient color
  vec3 ambient=.25+.25*Background(n);

  // Shadow computation
  float shadow=1.-float(ombredouce(p+0.1*n,lp,2.,100));
  // Phong diffuse
  vec3 diffuse=.35*clamp(dot(n,l),0.,1.)*vec3(1.,1.,1.);

  // Specular
  vec3 r=reflect(e,n);
  vec3 specular=.12*pow(clamp(dot(r,l),0.,1.),10.)*vec3(1.,1.,1.);
  vec3 c=ambient+shadow*(diffuse+specular);
  return c;
}

// Image
void mainImage(out vec4 color,in vec2 pxy)  
{
  // Pixel
  vec2 pixel=(-iResolution.xy+2.*pxy)/iResolution.y;

  // Mouse
  vec2 m=iMouse.xy/iResolution.xy;

  // Camera
  Ray ray=CreateRay(m,pixel);


  // Hit and number of steps
  bool hit;
  int s;

   // Trace ray
 float t=SphereTrace(ray,75.,hit,s);

  // Shade background
  vec3 rgb=Background(ray.rd);

  if(hit)
  {
    // Position
    vec3 p=Point(ray,t);

    // Compute normal
    vec3 n=ObjectNormal(p);

    // Shade object with light
    rgb=Shade(p,n,ray.rd);
  }

  color=vec4(rgb,1.);
}
