#define IMAX   150.

void mainImage(out vec4 O, vec2 U) {
  vec2 zoom = vec2(-.743643887037151, .131825904205330);

  vec2 R = iResolution.xy;
  U = (U * 2. - R) / R.y / exp(iTime);
  U += zoom;

  vec2 zn = vec2(0);

  float i = 1.;
  for(; i <= IMAX && dot(zn, zn) < 6.; i++) {
    zn = mat2(zn, -zn.y, zn.x) * zn + U;
  }

  vec3 col = vec3(1. - mod(i, 2.));

  O = vec4(col, 1);
}
