void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv0 = fragCoord.xy / iResolution.xy;
    vec2 uv = uv0;
    float timeScale = 1.3;

    uv.x *= iResolution.x / iResolution.y;
    uv -= 0.5 * sin(iTime*timeScale);

    float angle = iTime/4. * timeScale * 0.5; // Adjust the multiplier for speed
    float cosA = cos(angle);
    float sinA = sin(angle);
    mat2 rotation = mat2(cosA, -sinA, sinA, cosA);
    uv *= rotation;

    float size = 5.0;
    vec2 a = mod(uv, 1.0 / size) * size;
    float mv = 0.;//(a.y >= 0.5) ? -iTime * timeScale : iTime * timeScale;

    uv.x += mv;
    a = round(mod(uv, 1.0 / size) * size);

    float v = abs(a.x - a.y);

    float edgeThickness = 75.0 / iResolution.x;
    float distToEdge = min(mod(uv.x * size, 0.5), mod(uv.y * size, 0.5));
    float outline = step(edgeThickness, distToEdge);

    vec3 tex = 1.-max(texture(iChannel0,uv0).rgb,0.);

    // Combine white square and outline
    vec3 color = mix(tex, vec3(v), outline);
    if (fract(iTime/2.)>2./3.)
        color = mix(tex, vec3(1.-v), outline);
    else if (fract(iTime/2.)>1./3.)
        color = mix(tex, vec3(0.), outline);

    // Output final color
    fragColor = vec4(color, 1.0);
}
