#define R iResolution.xy
#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define B(U) texelFetch(iChannel1,ivec2(U),0)
#define C(U) texelFetch(iChannel2,ivec2(U),0)
#define D(U) texelFetch(iChannel3,ivec2(U),0)

#define M 16.
#define Main void mainImage(out vec4 Q, in vec2 U) 

float line (vec2 u, vec2 a, vec2 b) {
    return length(u-a-(b-a)*clamp(dot(u-a,b-a)/dot(b-a,b-a),0.,1.));
}
float sort (vec2 u, vec2 a, vec2 b) {
    if (a.x<0.||a.y<1.||b.x<0.||b.y<0.) return 1e9;
    float r = length(a-b);
    float l = length(u-a-(b-a)*clamp(dot(u-a,b-a)/dot(b-a,b-a),0.,1.));

    return l > .5 ? 100.*l : r;
}

vec4 hash(vec4 p4)
{
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}
#define normaliz(v) (length(v)>0.?normalize(v):vec2(0))

Main {
    U = floor(U);
    vec2 u = mod(U,M);
    if (abs(u.x-.5*M)>.1||abs(u.y-.5*M)>.1) discard;

    Q = A(U);
    float cQ = 0.-sign(hash(U.xyxy).x*2.-1.);
    vec2 f = vec2(Q.xy-0.5*R);
    f = -.0001*f/(1.+length(f));
    for (float x = -4.; x<=4.;x++)
    for (float y = -4.; y<=4.;y++) if (abs(x)>0.||abs(y)>0.) {
        vec4 c = D(U+vec2(x,y));
        if (c.z>0.) {
            vec4 a = A(c.xy);
            vec4 b = A(c.zw);
            float cb = sign(hash(c.zwzw).x*2.-1.);
            {
              vec2 r = b.xy-Q.xy;
              float l = length(r);
              float s = line(Q.xy+vec2(x,y),a.xy,b.xy);
              if (l > 0. && s < .5) {
                  f += 5e-2*cQ*cb*r/l/l-10.*r/l/l/l/l/l;
                  f += .25*(b.zw-Q.zw)*abs(dot(normaliz(b.zw-Q.zw),r/l))*smoothstep(10.,3.,l);
              }
            }
        }
    }

    Q.zw += f - .0*Q.zw;
    Q.xy += f + Q.zw;

    if (Q.x<=1.) Q.x = 1., Q.z = abs(Q.z);
    if (Q.y<=1.) Q.y = 1., Q.w = abs(Q.w);
    if (Q.x>R.x-1.) Q.x = R.x-1., Q.z = -abs(Q.z);
    if (Q.y>R.y-1.) Q.y = R.y-1., Q.w = -abs(Q.w);
    vec2 m = (Q.xy-iMouse.xy);
    if (iMouse.z>0.) Q.zw += m/dot(m,m);

    if (iFrame < 1) {
        Q = vec4(U,.0*sin(U.x),.0*cos(U.y+U.x));
    }


}

void swap (inout vec4 Q, in vec2 U, vec2 u) {
    vec4 q = B(U+u);
    vec4 a = A(q.xy);
    vec4 a0= A(Q.xy);
    if (length(a.xy-U) < length(a0.xy-U)) 
        Q = q;
}
Main {
    U = floor(U);
    Q = B(U);
    swap(Q,U,vec2(1,0));
    swap(Q,U,vec2(0,1));
    swap(Q,U,vec2(-1,0));
    swap(Q,U,vec2(0,-1));

    swap(Q,U,vec2(2,0));
    swap(Q,U,vec2(0,2));
    swap(Q,U,vec2(-2,0));
    swap(Q,U,vec2(0,-2));

    Q.zw = Q.xy;

    if (iFrame < 1) {
        vec2 u = round(U/M)*M+.5*M;
        Q = vec4(U,0,0);
    }
}

void swap (inout vec4 Q, in vec2 U, vec4 q) {
    vec4 a = A(q.xy);
    vec4 b = A(q.zw);
    vec4 a0= A(Q.xy);
    vec4 b0= A(Q.zw);
    float ab = sort(U,a.xy,b.xy);
    float a0b0 = sort(U,a0.xy,b0.xy);
    float ab0 = sort(U,a.xy,b0.xy);
    float a0b = sort(U,a0.xy,b.xy);

    if (ab <= a0b0 && ab < ab0 && ab < a0b) 
        Q = q;
    else if (ab0 <= a0b0 && ab0 < a0b) 
        Q.xy = q.xy;
    else if (a0b <= a0b0)
        Q.zw = q.zw;

}
