    swap(Q,U,B(U+vec2(0,-2)));
    swap(Q,U,B(U+vec2(0,2)));
    swap(Q,U,B(U+vec2(3,0)));
    swap(Q,U,B(U+vec2(-3,0)));
    swap(Q,U,B(U+vec2(0,-3)));
    swap(Q,U,B(U+vec2(0,3)));




    if (iFrame < 1) {
        vec2 u = round(U/M)*M+.5*M;
        Q = vec4(u,u);
    }

}

void swap (inout vec4 Q, in vec2 U, vec4 q) {
    vec4 a = A(q.xy);
    vec4 b = A(q.zw);
    vec4 a0= A(Q.xy);
    vec4 b0= A(Q.zw);
    float a0a = sort(U,a0.xy,a.xy);
    float a0b0 = sort(U,a0.xy,b0.xy);
    float a0b = sort(U,a0.xy,b.xy);

    if (a0a <= a0b0 && a0a < a0b) 
        Q.zw = q.xy;
    else if (a0b <= a0b0)
        Q.zw = q.zw;

}
