float numIterations = 2.0;
float glowModifier = .002;
float fractionMultiplier = 3.;
float burstModifier = 120.0;
float speedModifier = 1.;
float densityModifier = .05;
float highlightModifier = 1.4;







float dot2(in vec2 v ) { return dot(v,v); }

float Heart( in vec2 p )
{
    p.x = abs(p.x);

    if( p.y+p.x>1.0 )
        return sqrt(dot2(p-vec2(0.25,0.75))) - sqrt(2.0)/4.0;
    return sqrt(min(dot2(p-vec2(0.00,1.00)),
                    dot2(p-0.5*max(p.x+p.y,0.0)))) * sign(p.x-p.y);
}

vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
       vec2 uv = (fragCoord * densityModifier - iResolution.xy) / iResolution.y;
       vec2 uvOrigin = uv;
       vec3 finalColour = vec3(0.0);

       for (float i = 0.0; i < numIterations; i++){
           uv = fract(uv * fractionMultiplier) - 0.5;

           float d = length(uv) * Heart(uv);

           vec3 colour = palette( uv.x, vec3(1.0,0.1,0.5),vec3(1.0,0.1,0.5),vec3(1.0,1.0,0.1),vec3(1.0,0.10,0.90));

           d = sin(d*burstModifier + (iTime * speedModifier)) / burstModifier;
           d = abs(d);

           d = pow(glowModifier / d, highlightModifier);


           finalColour += colour * d;
       }

       fragColor = vec4(finalColour, 1.0);
}
