vec2 refine(float xx, float yy, float time) 
{
    float ax = xx;
    float ay = yy;
    return vec2(
        ax,
        ay
    );
}



float check1(vec2 p, float t, float s) {
    t = (t/1.0)*s;
    float x = (p.x);
    float y = (p.y);
    return abs(
    pow(
        abs(sin(
            x/y
        )),
        cos(1.0/x)+pow(pow(abs(sin(t+x/(cos(x)+1.0))),sin((t+1.0)/pow((x*y),cos(t/cos(pow(x,sin(x*y))+0.05))))),cos(t))

    ));
}

float check2(vec2 p, float t, float s) {
    t = (t/4.0)*s;
    float x = (p.x);
    float y = (p.y);
    return abs(
    pow(
        abs(sin(
            x/y
        )),
        cos(1.0/x)+pow(pow(abs(sin(t+x/(cos(x)+1.5))),sin((t+1.5)/pow((x*y),cos(t/cos(pow(x,sin(x*y))+0.1))))),cos(t))

    ));
}

float check3(vec2 p, float t, float s) {
    t = (t/3.0)*s;
    float x = (p.x);
    float y = (p.y);
    return abs(
    pow(
        abs(sin(
            x/y
        )),
        cos(1.0/x)+pow(pow(abs(sin(t+x/(cos(x)+2.0))),sin((t+2.0)/pow((x*y),cos(t/cos(pow(x,sin(x*y))+0.2))))),cos(t))

    ));
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    iMouse.zw;
    float mx = iMouse.zw.x;
    float my = iMouse.zw.y;

    // animation	
    float speed = 1.0;
    float div = 1.0;


    if (mx>1.0) {
        div -= 0.01;
    }

	float t = iTime/div;
    vec3 color = vec3(0.5);
    float threshold = 0.1;

    //get point
    //Base: 538, 3000
    vec2 point = vec2(
    //(t/4.0) + 
    (fragCoord.x/(538.0)),
    //(t/18.4) + 
    (fragCoord.y/(3000.0))
    );

    // iterate
    for( int v=0; v<3; v++   ) {
        float current = 0.5;
        for( int i=0; i<1; i++ )
        {
            float value;
            if (v==0) {
                value = check1(point,t,speed);
                //value = 0.0;
            } else if (v==1) {
                value = check2(point,t,speed);
                //value = 0.0;
            } else if (v==2) {
                value = check3(point,t,speed);
                //value = 0.0;
            }

            current = value;
            if(abs(value) > 1.0-threshold) {
                current -= float(1);
                break;     
            } else if (abs(value) < threshold) {
                current += float(1);
                break;   
            }

            point = refine(float(point.x),float(point.y),t);
        }

        if (v==0) {
            color.r = current;
        } else if (v==1) {
            color.g = current;
        } else if (v==2) {
            color.b = current;
        }
    }

    fragColor = vec4(color.r, color.g, color.b, 1.0 );
}
