vec2 rand(vec2 co){
    return vec2(
        fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453),
        fract(cos(dot(co.yx ,vec2(8.64947,45.097))) * 43758.5453)
    )*2.0-1.0;
}

float dots(in vec2 uv)
{
    vec2 g = floor(uv);
    vec2 f = fract(uv)*4.0-2.0;
    vec2 r = rand(g)*3.5;

    return length(f+r) + 0.1 * abs(cos(0.7 * iTime + rand(uv).x));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy - 0.5;
	uv.x *= iResolution.x/iResolution.y;

    vec2 mouse = (iMouse.xy / iResolution.xy - 0.5) * iResolution.x/iResolution.y;

    float d = smoothstep(.2, .22,dots((uv + 0.4 * mouse) * 20.0));
    d = min(smoothstep(.2, .22,dots((uv + mouse) * 15.0)), d);
    d = min(smoothstep(.2, .22,dots((uv + 1.5 * mouse) * 14.0)), d);
    d = min(smoothstep(.2, .22,dots((uv + 0.1 * mouse) * 25.0)), d);

	fragColor = vec4(vec3(1. - d ), 1.0);
}
