float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}

float nrand(vec2 uv)
{
    return fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453);
}

vec3 dither(vec2 uv)
{
    float r = nrand(uv) + nrand(uv + vec2(1.1)) - 0.5;
    return vec3(r / 255.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 goggleUV = uv;

    goggleUV.x = mod(uv.x, 0.5);

    goggleUV -= vec2(0.25, 0.5);

    float goggle = sdCircle(goggleUV, 0.2);

    goggle = abs(goggle - 1.0);

    goggle = smoothstep(0.76, 0.9, goggle);

    vec3 goggle3 = vec3(goggle);

    goggle3 += dither(fragCoord);

    vec3 london = texture(iChannel0, uv).rgb;
    london = mix(london, vec3(dot(vec3(0.200, 0.500, 0.100), london)), 0.8);

    fragColor = vec4(london * goggle3,1.0);
}
