#define R3D(a) mix(q*dot(p,q), p, cos(a)) + sin(a)*cross(p,q)
#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p,q=vec3(-1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,1)*e;
    p.z-=2.;
    p=R3D(iTime);

    for(j=0.;j++<6.;)
        p.xz=abs(abs(p.xz*R2D(i/j))-.1)-.05,
        d=max(max(p.z,p.x),p.y),
        d-=dot(cos(p.xy),sin(p.zy))/2e4;

    e+=d;

}o+=vec4(sqrt(p/d),1)*3./i;
}
