void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //fixes stretching and makes center of screen (0,0)
    vec2 uv = ((fragCoord/iResolution.xy)-.5)*2.;
    uv.x *= iResolution.x/iResolution.y;

    //assigns crc with length from the center of screen and subtracts it by .5
    float crc = length(uv) - .5;

    //complex antialiasing code
    crc = smoothstep(0.,5./iResolution.y,crc);

    //makes circle visual
    fragColor = vec4(crc,crc,1.,1.);
}
