#define PI 3.1415926538


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float fny;
    float mx = uv.x*10.0 + iTime*2.0;

    float fny1 = 0.5+0.3*sin(mx);
    float fny2 = 0.5+0.3*cos(mx);
    float fny3 = 0.5+0.3*sin(mx+PI);
    float fny4 = 0.5+0.3*sin(mx+ 3.0*PI/2.0);

    if ((uv.y >= fny1 - 0.02 && uv.y <= fny1 + 0.04) ||
        (uv.y >= fny2 - 0.02 && uv.y <= fny2 + 0.04) ||
        (uv.y >= fny3 - 0.02 && uv.y <= fny3 + 0.04) ||
        (uv.y >= fny4 - 0.02 && uv.y <= fny4 + 0.04))
        fny = 1.0;
    else 
        fny = 0.0;



    // Output to screen
    fragColor = vec4(fny, col);
}
