// Grid size in pixels
float GRID_SIZE = 22.0;
// Line width or dot size
float WIDTH = 1.0;
// Grid type
// 0 - lines
// 1 - dots
int GRID_TYPE = 0;


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 frac = mod(fragCoord.xy, GRID_SIZE);
    vec2 edge = step(WIDTH, frac.xy);
    float value;
    if (GRID_TYPE == 0) {
        value = min(edge.x, edge.y);
    } else if (GRID_TYPE == 1) {
        value = max(edge.x, edge.y);
    }
    fragColor.rgb = vec3(value);
}
