Main {
    T = iTime;
    if (iFrame%6!=5) discard;
    Q = A(U*.8+.1*R);
    //Q = vec4(length(Q.xy),length(Q),length(Q.zw),1);
    Q = vec4(length(Q.xy),.707*length(Q),length(Q.zw),1);

   //Q = scene(2.*(U-.5*R)/R.y);


}

#define R iResolution.xy
#define Main void mainImage(out vec4 Q, in vec2 U)
#define A(U) texelFetch(iChannel0,ivec2(U),0)
float T;

#define pi 3.14159265359
#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))

vec4 light(vec3 p, vec3 d) {
    float l = length(d-normalize(vec3(-p.x,1,-p.y+1.5)));
    return 
        exp(-.1-.04*p.z-.04*length(p))*((.01+.01*sin(2.5*d.x+p.x)*sin(10.*p.z+1.4*d.z))+
            (.05+.05*sin(.3*d.x+p.x)*sin(p.z+.2*d.z))+
            exp(-.1-.02*p.z)*(.01+.01*sin(14.*d.x-9.*p.x)*sin(-14.*p.z+13.*d.z))+
            (.05+.05*sin(-14.*d.x+18.*p.x)*sin(-5.*p.z+10.*d.z))+
    ((.5+.5*d.y)*vec4(.5,0,1.5,4))*.5*(exp(-.5*l*l))+
    55.*(.5+.5*vec4(3,2,1,4))/(1.+10000.*l*l));
}
float w (float x) {
    float s = sin(x);
    return 1.*exp(.7*(s*s*s));
}

float map (vec3 p) {
    p.xz *= 1.8;
    float a = .1*w(4.*p.x+p.z+1.*T)+
        .2*w(1.*p.x-1.*p.z-.5*T)*w(.4*p.x-.1*p.z)+
        .2*w(-1.*p.x-1.*p.z+.5*T)+
        .1*w(-3.*p.x-2.*p.z-.5*T)*w(p.x+p.z)+
        .1*w(1.*p.x+5.*p.z-1.*T)+
        .1*w(3.*p.x-6.*p.z+.5*T)+
        .02*w(15.*p.x-12.*p.z-.5*T)+
        .05*w(-13.*p.x+5.*p.z+.5*T)+
        .006*w(-13.*p.x-15.*p.z+.5*T);

    return .5*abs(p.y - 
        .1*exp(.5*a*a)+.1

        )-.01;
}
vec3 normal(vec3 p) {
    vec2 e = vec2(1e-4,0);
    float m = map(p);
    return normalize(vec3(
        map(p+e.xyy)-m,
        map(p+e.yxy)-m,
        map(p+e.yyx)-m
    ));
}
vec4 scene (vec2 u) {


    vec3 p = vec3(0,0,-2);
    vec3 d = normalize(vec3(u,4.));
    p.y += .2;
    p.yz *= ei(-.25);
    d.yz *= ei(-.25);
    vec3 o = p;
    for (float i = 0.; i < 150.; i++){
        float m = map(p);
        p += d*m;
        if (m < .001) {
            d = reflect(d,normal(p));
            o = p;
            p += d*.005;
        }
        if (m > 20.) return light(o,d);

    }
    return light(o,d);
}

Main {
    T = iTime;
    int I = iFrame % ( 1 + 2 + 1 + 2 );
    U = floor(U);
    vec2 u = 2.*(U-.5*R)/R.y;
    if (I == 0) {
        // Phase Space
        Q = scene(u);
        Q.x = Q.x+.5*Q.y;
        Q.y = 0.;
        Q.z = Q.z+.5*Q.y;
        Q.w = 0.;
    } else if (I == 1) {
        // x forward pass
        Q = vec4(0);
        vec2 w = 2.*pi*vec2(U)/R;
        for (float i = 0.; i < R.x; i++) {
            vec4 a = A(vec2(i,U.y));
            Q.xy += a.xy*ei(w.x*i);
            Q.zw += a.zw*ei(w.x*i);
        }
    } else if (I == 2) {
        // y forward pass
        Q = vec4(0);
        vec2 w = 2.*pi*vec2(U)/R;
        for (float i = 0.; i < R.y; i++) {
            vec4 a = A(vec2(U.x,i));
            Q.xy += a.xy*ei(w.y*i);
            Q.zw += a.zw*ei(w.y*i);
        }
    } else if (I == 3) {
        // Frequency Space
        vec2 w = 2.*pi*(fract(vec2(U)/R+.5)*2.-1.);
        Q = A(U);
        w *= .2;
        bool b = true;
        bool b2 = true;
        for (float i = 0.; i < 7.; i++) {
            vec2 v = w*ei(i/7.*2.*pi);
            b = b&&(v.x<1.);
            b2 = b2&&(v.x<.8);
        }
        Q.xy *= .5*(1.5*float(b)+.5*float(b2));
        Q.zw *= .5*(.5*float(b)+float(b2));
        //Q.xy = Q.xy*ei(1.0*(length(w)*100.*sin(iTime)));
        //

        //Q.zw = Q.zw*ei(dt*length(w));
    } else if (I == 4) {
        // x reverse pass
        Q = vec4(0);
        vec2 w = 2.*pi*vec2(U)/R;
        for (float i = 0.; i < R.x; i++) {
            Q.xy += A(vec2(i,U.y)).xy*ei(-w.x*i);
            Q.zw += A(vec2(i,U.y)).zw*ei(-w.x*i);
        }
        Q /= R.x;
    } else if (I == 5) {
        // y reverse pass
        Q = vec4(0);
        vec2 w = 2.*pi*vec2(U)/R;
        for (float i = 0.; i < R.y; i++) {
            Q.xy += A(vec2(U.x,i)).xy*ei(-w.y*i);
            Q.zw += A(vec2(U.x,i)).zw*ei(-w.y*i);
        }
        Q /= R.y;
    }

}
