renderpass.code:
float Equation(vec2 coord)
{
    float x = coord.x;
    float y = coord.y;
    return tan(x)*cos(y)+tan(x)*sin(y)-sin(x);
}
bool Satisfied(vec2 coord)
{
    return Equation(coord) < 1.0;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 center = vec2(0.0,0.0);//画面中心坐标
    float scaler =16.0;//视野大小

    vec2 coord = fragCoord - iResolution.xy * .5;
    coord /= iResolution.y * .5;
    coord *= scaler;
    coord += center;
    if(Satisfied(coord))
        fragColor = vec4(1.0,1.0,1.0,1.0);
    else if(abs(mod(coord.x,0.2)) < 0.005 || abs(mod(coord.y,0.2)) < 0.005)
    {
        if(abs(coord.y) < 0.005 || abs(coord.x) < 0.005)
            fragColor =  vec4(1.0,1.0,1.0,1.0) * 0.75;
        else if(abs(mod(coord.x,1.0)) < 0.005 || abs(mod(coord.y,1.0)) < 0.005)
            fragColor =  vec4(1.0,1.0,1.0,1.0) * 0.5;
        else
            fragColor =  vec4(1.0,1.0,1.0,1.0) * 0.25;
    }
    else
        fragColor = vec4(0,0,0,0);

}
