vec2 Pol(vec2 p){
    float a = atan(p.x, p.y);
    float d = length(p);
    return vec2(a, d);
}

float N21(vec2 p){
    return fract(dot(p.x*46.23, p.y*47.42)*47.411+93.23);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv *= 10.;
    vec2 gv = fract(uv)-.5;
    //gv *= 1./length(uv);
    /* //fun spiral effect
    float d = length(uv);
    uv *= mat2(cos(d), -sin(d), sin(d), cos(d));
    */
    vec2 id = floor(uv);
    vec3 col = vec3(0);
    vec2 pv = Pol(gv);


    float t = iTime;
    vec3 base = sin(t*vec3(0.276, 0.891, 0.461)+length(uv))*.4+.8; 
    float fade = mix(.0, -3., sin(id.x*Pol(id).x+.1+t));
    float size = N21(id+.1)*5.;
    float wheel = fract(pv.x+t) - floor(pv.y*size)*.5;

    col += wheel*base*fade;
    col.rgb+=.3;
    fragColor = vec4(col,1.0);
}
