float Cirque(vec2 uv,float r1,float r2,float blur)
{
    float d = length(uv);
    return smoothstep(r1,r1+blur,d)*smoothstep(r2,r2-blur,d);
}
float Rectangle(vec2 uv,float r,float bottom,float top,float blur,float x)
{
    float d = length(uv);
    float v = step(d,r);
    return smoothstep(bottom,bottom+blur,x)*smoothstep(top,top-blur,x)*v;
}

vec2 rotate(vec2 uv,float angle/*弧度*/)
{
    mat2 m_rotate =mat2(cos(angle),sin(angle),-sin(angle),cos(angle));
    uv = uv*m_rotate;
    return uv;
}
float Sector(vec2 uv,float r,float outAngle,float inAngle,float speed)
{

   float d = length(uv);
   float v = step(d,r);
   uv = rotate(uv,iTime*speed);
   float uvAngle = atan(uv.y,uv.x); 

   outAngle = radians(outAngle)*0.5;
   inAngle = radians(inAngle)*0.5;
   return (smoothstep(outAngle,inAngle,uvAngle)-smoothstep(-inAngle,-outAngle,uvAngle))*v;

}
float Circle(vec2 uv,float r)
{
   float d = length(uv);
   float v = step(d,r);
   return v;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x = uv.x*iResolution.x/iResolution.y;
    vec3 col = vec3(Cirque(uv,0.1,0.14,0.004))+ vec3(Cirque(uv,0.22,0.26,0.004))+vec3(Cirque(uv,0.36,0.4+0.004,0.004));
    // Output to screen
    col += Rectangle(uv,0.4,-0.015,0.015,0.0006,uv.y)+Rectangle(uv,0.4,-0.015,0.015,0.0001,uv.x);
    col += mix(vec3(0.0,1.0,0.0),vec3(1.0,0.0,0.0),Sector(uv,0.4,45.0,20.0,2.0))*Circle(uv,0.4) ;
    fragColor = vec4(col,1.0);
}
