float noise1d(float n){
    return fract(cos(n*89.42)*343.42);
}
float noise2d(vec2 co){
  return fract(sin(dot(co.xy ,vec2(1.0,73))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    float r = 0.07;
    float k = 0.19;
    float r2 = 0.05;
    float k2 = 0.16;
    float r3 = 0.06;
    float r4 = 0.07;

    uv = uv*5.0;

    vec2 iuv = trunc(uv);
    uv = fract(uv) - 0.5;

    float value = noise2d(iuv);

    vec2 center = vec2(sin(iTime*value*2.2 - value*5.0)*0.27, cos(iTime*1.5 + value*3.0)*0.23);
    float circle = smoothstep(r + k, r, length(uv - center));

    float value1 = noise1d(5.0*value);
    vec2 center1 = vec2(cos(iTime*0.5 + value*6.5)*0.26, sin(iTime*1.5*value + value1*3.5)*0.31);
    circle += smoothstep(r2 + k, r2, length(uv - center1));

    float value2 = noise1d(1.95 - value);
    vec2 center2 = vec2(sin(iTime*1.6 - value*3.14)*0.33, sin(iTime*0.95 + value2*3.5)*0.33);
    circle += smoothstep(r3 + k2, r3, length(uv - center2));

    float value3 = noise1d(150.0*value + 56.0);

    vec2 center3 = vec2(sin(iTime*0.8 - value3*0.85)*0.28*value, sin(iTime*0.7 + value*6.5)*0.15);
    circle += smoothstep(r4 + k2, r4, length(uv - center3));

    if (circle >= 1.0) {
        circle = 0.2;
    };

    circle = smoothstep(0.1, 0.5, circle);

    float fade = sin(iTime*value*2.0 + value*3.5)*0.5 + 0.4;
    float fade2 = cos(value*4.0 - iTime*1.2);

    float colorR = circle*fade;
    float colorG = circle*fade2;
    fragColor = vec4(colorR, colorG, 0.0, 0.0)*fade;
}
