float dot2( vec2 v ) { return dot(v,v); }

float sdfCoolS( in vec2 p )
{
    float six = (p.y<0.0) ? -p.x : p.x;
    p.x = abs(p.x);
    p.y = abs(p.y) - 0.2;
    float rex = p.x - min(round(p.x/0.4),0.4);
    float aby = abs(p.y-0.2)-0.6;

    float d = dot2(vec2(six,-p.y)-clamp(0.5*(six-p.y),0.0,0.2));
    d = min(d,dot2(vec2(p.x,-aby)-clamp(0.5*(p.x-aby),0.0,0.4)));
    d = min(d,dot2(vec2(rex,p.y  -clamp(p.y          ,0.0,0.4))));

    float s = 2.0*p.x + aby + abs(aby+0.4) - 0.4;
    return sqrt(d) * sign(s);
}

vec3 s_curve(vec3 x)
{
    float a = 2.51f;
    float b = 0.03f;
    float c = 2.43f;
    float d = 0.59f;
    float e = 0.14f;
    x = max(x, 0.0);
    return clamp((x*(a*x+b))/(x*(c*x+d)+e),0.0,1.0);
}
vec3 d_s_curve(vec3 x)
{
    float a = 2.51f;
    float b = 0.03f;
    float c = 2.43f;
    float d = 0.59f;
    float e = 0.14f;

    x = max(x, 0.0);
    vec3 r = (x*(c*x + d) + e);
    return (a*x*(d*x + 2.0*e) + b*(e - c*x*x))/(r*r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 3.0;
    vec2 uv = fragCoord/iResolution.xy;
    uv *= scale;

    // Time varying pixel color
    vec3 c = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,4, 3));
    float d = sdfCoolS(uv - (0.5 * scale));

    vec3 col = abs(d) / c; // abs(d) - c

    col = sqrt(col);
    col = d_s_curve(col);

    col = tanh(col);

    fragColor = vec4(col, 1.0);
}
