#define PI 3.1415926538

vec2 rotate(vec2 v, float angle){
    float s = sin(angle);
    float c = cos(angle);
    mat2 m = mat2(c, s, -s, c);
    return v * m;
}

float sdCircle(vec2 p, float r){
    return length(p) - r;
}

float sdBox(vec2 p, vec2 b){
    vec2 d = abs(p) - b;
    return  length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}
void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = (fragCoord * 2. - iResolution.xy)/iResolution.y;
    vec2 uv2 = uv;
    uv = fract(uv * 3.0) - 0.5;
    uv = rotate(uv, radians(45.));
    vec3 color = vec3(0.25, 0.25, 0.35) * fragCoord.x/iResolution.x;
    vec3 color2 = vec3(1., 1., 0.0) * (1.7 -abs(cos(iTime+uv2.y* 2.0)));//0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec2 p = uv;

    float r1 = 0.3;
    float circle1 = sdCircle(uv+vec2(-0.0, -0.0), r1);
    color = mix(color, color2, 1.0-smoothstep(0.0,0.02,abs(circle1)));

    float r2 = 0.24;
    p = uv;
    p = rotate(p, iTime);
    p = p + vec2(-0.0,-0.25);
    float circle2 = sdCircle(p, r2);
    color = mix(color, color2, 1.0-smoothstep(0.0,0.02,abs(circle2)));

    float r3 = 0.24;
    p = uv;
    p = rotate(p, iTime);
    p = p + vec2(-0.0,0.25);
    float circle3 = sdCircle(p, r3);
    color = mix(color, color2, 1.0-smoothstep(0.0,0.02,abs(circle3)));

    float r4 = 0.6;
    float circle4 = sdCircle(uv+vec2(-0.0, 0.00), r4);
    color = mix(color, color2, 1.0-smoothstep(0.0,0.02,abs(circle4)));

    p = uv;
    p = p + vec2(0.0, -0.49);
    p = rotate(p, radians(45.));
    vec2 b = vec2(0.35, 0.35);
    float box = sdBox(p, b);
    color = mix(color, color2, 0.8-smoothstep(0.0,0.02,abs(box)));

    vec2 p2 = uv ;
    p2 = p2 + vec2(0.0, 0.5);
    p2 = rotate(p2, radians(45.));
    vec2 b2 = vec2(0.35, 0.35);
    float box2 = sdBox(p2, b2);
    color = mix(color, color2, 0.8 - smoothstep(0.0,0.02,abs(box2)));


    fragColor = vec4(color, 1.0);
}
