float ssmoothstep(in float x);
#define paint(dist, col) color = mix(color, col, ssmoothstep(dist))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = fragCoord/iResolution.xy;
    vec2 uv = (fragCoord.xy-0.5 * iResolution.xy) / iResolution.xy*5.0 * log(iTime); 
    uv.x += sin(iTime);
    vec3 color = vec3(0.0,0.0,0.0);
    float unit = 5.0 / iResolution.y;
    float dist = abs(fract(uv.x + 0.5) - 0.5);
    paint(dist, vec3(1.0,0.0,0.0));

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    if(abs(uv.x) < 0.1){
    color = vec3(1.0);
    }

    fragColor = vec4(color, 1.0);
}
float ssmoothstep(in float x) {
    if (x < 0.01) return 1.0;
    else return 0.0;
}
