void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    vec3 tex_col = texture(iChannel0, uv).rgb;
    vec2 offset_uv = uv;
    float lum = 0.299*tex_col.r + 0.587*tex_col.g + 0.114*tex_col.b;

    offset_uv.y = offset_uv.y - lum * 0.2;
    float stripe_factor = texture(iChannel1, offset_uv).r;
    vec3 stripe_color = vec3(tex_col.r * stripe_factor, tex_col.g * stripe_factor, tex_col.b * stripe_factor);

    // Output to screen
    fragColor = vec4(stripe_color,1.0);
}

#ifdef GL_ES
precision mediump float;
#endif

float normpdf(in float x, in float sigma)
{
	return 0.39894*exp(-0.5*x*x/(sigma*sigma))/sigma;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 c = texture(iChannel0, fragCoord.xy / iResolution.xy).rgb;

    //declare stuff
    const int mSize = 11;
    const int kSize = (mSize-1)/2;
    float kernel[mSize];
    vec3 final_colour = vec3(0.0);

    //create the 1-D kernel
    float sigma = 7.0;
    float Z = 0.0;
    for (int j = 0; j <= kSize; ++j)
    {
        kernel[kSize+j] = kernel[kSize-j] = normpdf(float(j), sigma);
    }

    //get the normalization factor (as the gaussian has been clamped)
    for (int j = 0; j < mSize; ++j)
    {
        Z += kernel[j];
    }

    //read out the texels
    for (int i=-kSize; i <= kSize; ++i)
    {
        for (int j=-kSize; j <= kSize; ++j)
        {
            final_colour += kernel[kSize+j]*kernel[kSize+i]*texture(iChannel0, (fragCoord.xy+vec2(float(i),float(j))) / iResolution.xy).rgb;

        }
    }


    fragColor = vec4(final_colour/(Z*Z), 1.0);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float factor = 400.0;
    float stripe = sin(uv.y * factor);
    fragColor = vec4(stripe, stripe, stripe,1.0);
}
